                &#1079;&#1077;&#1083;&#1077;&#1085;&#1072;&#1090;&#1072; &#1084;&#1086;&#1088;&#1089;&#1082;&#1072; &#1082;&#1086;&#1089;&#1090;&#1077;&#1085;&#1091;&#1088;&#1082;&#1072; &#1077; &#1085;&#1072;&#1081;- &#1095;&#1077;&#1089;&#1090;&#1086; &#1089;&#1088;&#1077;&#1097;&#1072;&#1085;&#1072;&#1090;&#1072;, &#1074;&#1098;&#1087;&#1088;&#1077;&#1082;&#1080; &#1090;&#1086;&#1074;&#1072; &#1087;&#1086;&#1087;&#1091;&#1083;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072; &#1081; &#1077; &#1084;&#1085;&#1086;&#1075;&#1086; &#1087;&#1086;- &#1084;&#1072;&#1083;&#1082;&#1086; &#1086;&#1090; &#1087;&#1086;&#1087;&#1091;&#1083;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072;, &#1082;&#1086;&#1103;&#1090;&#1086; &#1077; &#1073;&#1080;&#1083;&#1072; &#1087;&#1088;&#1077;&#1076;&#1080;, &#1087;&#1086;&#1088;&#1072;&#1076;&#1080; &#1084;&#1072;&#1089;&#1086;&#1074;&#1080;&#1103; &#1083;&#1086;&#1074;, &#1087;&#1088;&#1077;&#1076;&#1096;&#1077;&#1089;&#1090;&#1074;&#1072;&#1085; &#1086;&#1090; &#1047;&#1072;&#1082;&#1086;&#1085; &#1079;&#1072; &#1047;&#1072;&#1089;&#1090;&#1088;&#1072;&#1096;&#1077;&#1085;&#1080;&#1090;&#1077; &#1042;&#1080;&#1076;&#1086;&#1074;&#1077; &#1087;&#1088;&#1077;&#1079; 1973; &#1073;&#1088;&#1072;&#1082;&#1086;&#1085;&#1080;&#1077;&#1088;&#1089;&#1090;&#1074;&#1086;&#1090;&#1086;, &#1079;&#1072;&#1089;&#1090;&#1088;&#1086;&#1103;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1082;&#1088;&#1072;&#1081;&#1073;&#1088;&#1077;&#1078;&#1085;&#1072;&#1090;&#1072; &#1080;&#1074;&#1080;&#1094;&#1072;, &#1091;&#1074;&#1077;&#1083;&#1080;&#1095;&#1072;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1085;&#1072;&#1089;&#1077;&#1083;&#1077;&#1085;&#1080;&#1077;&#1090;&#1086;, &#1079;&#1072;&#1084;&#1098;&#1088;&#1089;&#1103;&#1074;&#1072;&#1085;&#1077;&#1090;&#1086;, &#1083;&#1086;&#1074;&#1077;&#1085;&#1077;&#1090;&#1086; &#1085;&#1072; &#1088;&#1080;&#1073;&#1072; &#1089; &#1084;&#1088;&#1077;&#1078;&#1080;. &#1048;&#1084;&#1072; &#1075;&#1083;&#1086;&#1073;&#1080; &#1079;&#1072; &#1083;&#1086;&#1074;&#1091;&#1074;&#1072;&#1085;&#1077; &#1080;&#1083;&#1080; &#1085;&#1072;&#1088;&#1072;&#1085;&#1103;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1084;&#1086;&#1088;&#1089;&#1082;&#1080;&#1090;&#1077; &#1082;&#1086;&#1089;&#1090;&#1077;&#1085;&#1091;&#1088;&#1082;&#1080;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1089;&#1077; &#1088;&#1072;&#1074;&#1085;&#1103;&#1074;&#1072;&#1090; &#1085;&#1072; &#1089;&#1091;&#1084;&#1080; &#1076;&#1086; 100 000 &#1097;&#1072;&#1090;&#1089;&#1082;&#1080; &#1076;&#1086;&#1083;&#1072;&#1088;&#1072;. &#1055;&#1086;&#1085;&#1072;&#1089;&#1090;&#1086;&#1103;&#1097;&#1077;&#1084; &#1089;&#1087;&#1072;&#1089;&#1080;&#1090;&#1077;&#1083;&#1077;&#1085; &#1077;&#1082;&#1080;&#1087; &#1089;&#1077; &#1075;&#1088;&#1080;&#1078;&#1080; &#1087;&#1086;&#1087;&#1091;&#1083;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072; &#1085;&#1072; &#1084;&#1086;&#1088;&#1089;&#1082;&#1080;&#1090;&#1077; &#1082;&#1086;&#1089;&#1090;&#1077;&#1085;&#1091;&#1088;&#1082;&#1080; &#1076;&#1072; &#1089;&#1077; &#1091;&#1074;&#1077;&#1083;&#1080;&#1095;&#1080; &#1074;&#1098;&#1074; &#1074;&#1086;&#1076;&#1080;&#1090;&#1077; &#1086;&#1082;&#1086;&#1083;&#1086; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;.</p>
              <h3>&#1044;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;</h3>
              <p><strong><img border="0" src="../images/dolphin.jpg" align="right" alt="dolphins" width="148" height="125" /></strong>&#1058;&#1077;&#1079;&#1080; 
                &#1086;&#1095;&#1072;&#1088;&#1086;&#1074;&#1072;&#1090;&#1077;&#1083;&#1085;&#1080; &#1073;&#1086;&#1079;&#1072;&#1081;&#1085;&#1080;&#1094;&#1080; &#1089;&#1072; &#1080;&#1085;&#1090;&#1088;&#1080;&#1075;&#1091;&#1074;&#1072;&#1097;&#1080;, &#1075;&#1088;&#1072;&#1094;&#1080;&#1086;&#1079;&#1085;&#1080; &#1080; &#1079;&#1072;&#1073;&#1072;&#1074;&#1085;&#1080; &#1079;&#1072; &#1085;&#1072;&#1073;&#1083;&#1102;&#1076;&#1077;&#1085;&#1080;&#1077;. &#1058;&#1077; &#1088;&#1072;&#1079;&#1095;&#1080;&#1090;&#1072;&#1090; &#1085;&#1072; &#1085;&#1103;&#1082;&#1086;&#1083;&#1082;&#1086; &#1074;&#1080;&#1076;&#1072; &#1082;&#1086;&#1084;&#1091;&#1085;&#1080;&#1082;&#1072;&#1094;&#1080;&#1080;, &#1074;&#1082;&#1083;&#1102;&#1095;&#1080;&#1090;&#1077;&#1083;&#1085;&#1086; &#1087;&#1086;&#1076;&#1089;&#1074;&#1080;&#1088;&#1074;&#1072;&#1085;&#1077; &#1080; &#1080;&#1079;&#1087;&#1086;&#1083;&#1079;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1077;&#1079;&#1080;&#1082;&#1072; &#1085;&#1072; &#1090;&#1103;&#1083;&#1086;&#1090;&#1086; &#1080; &#1085;&#1072; &#1077;&#1093;&#1086; &#1083;&#1086;&#1082;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072; (&#1093;&#1080;&#1076;&#1088;&#1086;&#1083;&#1086;&#1082;&#1072;&#1090;&#1086;&#1088;), &#1079;&#1072; &#1076;&#1072; &#1085;&#1072;&#1084;&#1077;&#1088;&#1103;&#1090; &#1093;&#1088;&#1072;&#1085;&#1072;. &#1055;&#1077;&#1090;&#1085;&#1072;&#1076;&#1077;&#1089;&#1077;&#1090; &#1074;&#1080;&#1076;&#1072; &#1076;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;, &#1074;&#1082;&#1083;&#1102;&#1095;&#1080;&#1090;&#1077;&#1083;&#1085;&#1086; &#1080; &#1072;&#1092;&#1072;&#1083;&#1080;&#1090;&#1077; &#1080; &#1087;&#1077;&#1090;&#1085;&#1080;&#1089;&#1090;&#1080;&#1090;&#1077; &#1076;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;, &#1084;&#1086;&#1075;&#1072;&#1090; &#1076;&#1072; &#1073;&#1098;&#1076;&#1072;&#1090; &#1074;&#1080;&#1076;&#1077;&#1085;&#1080; &#1074; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1074;&#1086;&#1076;&#1080;. &#1053;&#1072;&#1081;- &#1074;&#1072;&#1078;&#1085;&#1086; &#1077; &#1076;&#1072; &#1079;&#1072;&#1087;&#1086;&#1084;&#1085;&#1080;&#1084; &#1089;&#1083;&#1077;&#1076;&#1085;&#1080;&#1090;&#1077; &#1087;&#1088;&#1072;&#1074;&#1080;&#1083;&#1072; :</p>
              <ul>
                <li>&#1053;&#1077; &#1089;&#1077; &#1086;&#1087;&#1080;&#1090;&#1074;&#1072;&#1081;&#1090;&#1077; &#1076;&#1072; &#1076;&#1086;&#1082;&#1086;&#1089;&#1074;&#1072;&#1090;&#1077; &#1076;&#1080;&#1074;&#1080; &#1076;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;- &#1079;&#1072;&#1073;&#1088;&#1072;&#1085;&#1077;&#1085;&#1086; &#1077;!&nbsp; &#1048;&#1084;&#1072; &#1088;&#1077;&#1076;&#1080;&#1094;&#1072; &#1087;&#1088;&#1086;&#1075;&#1088;&#1072;&#1084;&#1080;, &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1088;&#1072;&#1073;&#1086;&#1090;&#1103;&#1090; &#1089; &#1086;&#1087;&#1080;&#1090;&#1086;&#1084;&#1077;&#1085;&#1080; &#1076;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1084;&#1086;&#1078;&#1077;&#1090;&#1077; &#1076;&#1072; &#1076;&#1086;&#1082;&#1086;&#1089;&#1085;&#1077;&#1090;&#1077; &#1080; &#1076;&#1086;&#1088;&#1080; &#1076;&#1072; &#1089;&#1080; &#1087;&#1086;&#1080;&#1075;&#1088;&#1072;&#1077;&#1090;&#1077; &#1089; &#1090;&#1103;&#1093;. </li>
                <li>&#1053;&#1077; &#1087;&#1088;&#1072;&#1074;&#1077;&#1090;&#1077; &#1072;&#1075;&#1088;&#1077;&#1089;&#1080;&#1074;&#1085;&#1080;, &#1088;&#1077;&#1079;&#1082;&#1080; &#1080;&#1083;&#1080; &#1085;&#1077;&#1088;&#1074;&#1085;&#1080; &#1076;&#1074;&#1080;&#1078;&#1077;&#1085;&#1080;&#1103;.</li>
                <li>&#1055;&#1083;&#1091;&#1074;&#1072;&#1081;&#1090;&#1077; &#1091;&#1089;&#1087;&#1086;&#1088;&#1077;&#1076;&#1085;&#1086; &#1089; &#1076;&#1077;&#1083;&#1092;&#1080;&#1085;&#1080;&#1090;&#1077;. </li>
                <li>&#1055;&#1086;&#1084;&#1085;&#1077;&#1090;&#1077;, &#1095;&#1077; &#1074;&#1080;&#1077; &#1089;&#1090;&#1077; &#1085;&#1072;&#1074;&#1083;&#1077;&#1079;&#1083;&#1080; &#1074; &#1090;&#1103;&#1093;&#1085;&#1072;&#1090;&#1072; &#1090;&#1077;&#1088;&#1080;&#1090;&#1086;&#1088;&#1080;&#1103; &#1080; &#1077; &#1087;&#1086;- &#1076;&#1086;&#1073;&#1088;&#1077; &#1076;&#1072; &#1075;&#1080; &#1086;&#1089;&#1090;&#1072;&#1074;&#1080;&#1090;&#1077; &#1090;&#1077; &#1076;&#1072; &#1089;&#1077; &#1076;&#1086;&#1073;&#1083;&#1080;&#1078;&#1072;&#1090; &#1076;&#1086; &#1074;&#1072;&#1089; &#1080; &#1076;&#1072; &#1074;&#1080; &#1076;&#1086;&#1082;&#1086;&#1089;&#1085;&#1072;&#1090;, &#1086;&#1090;&#1082;&#1086;&#1083;&#1082;&#1086;&#1090;&#1086; &#1074;&#1080;&#1077; &#1076;&#1072; &#1087;&#1086;&#1089;&#1103;&#1075;&#1072;&#1090;&#1077; &#1082;&#1098;&#1084; &#1090;&#1103;&#1093;.</li>
              </ul>
              <p><strong>&#1058;&#1102;&#1083;&#1077;&#1085;&#1098;&#1090; &#1084;&#1086;&#1085;&#1072;&#1093;</strong></p>
              <p><strong> <img border="0" src="../images/monkseal.jpg" align="left" alt="monk seal" width="148" height="125" /></strong>&#1048;&#1084;&#1072; 
                3 &#1074;&#1080;&#1076;&#1072; &#1090;&#1102;&#1083;&#1077;&#1085;&#1080; &#1084;&#1086;&#1085;&#1072;&#1089;&#1080; &#1085;&#1072; &#1047;&#1077;&#1084;&#1103;&#1090;&#1072;- &#1050;&#1072;&#1088;&#1080;&#1073;&#1089;&#1082;&#1080;&#1103;&#1090;, &#1057;&#1088;&#1077;&#1076;&#1080;&#1079;&#1077;&#1084;&#1085;&#1086;&#1084;&#1086;&#1088;&#1089;&#1082;&#1080;&#1103;&#1090; &#1080; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103;&#1090;, &#1087;&#1086;&#1079;&#1085;&#1072;&#1090; &#1086;&#1097;&#1077; &#1082;&#1072;&#1090;&#1086; <strong class="subtitle">monachus schauinslandi</strong>. &#1057;&#1084;&#1103;&#1090;&#1072; &#1089;&#1077; &#1079;&#1072; 
                &#1077;&#1085;&#1076;&#1077;&#1084;&#1080;&#1095;&#1077;&#1085; &#1079;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1082;&#1086;&#1077;&#1090;&#1086; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072;, &#1095;&#1077; &#1090;&#1086;&#1079;&#1080; &#1074;&#1080;&#1076; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1089;&#1077; &#1089;&#1088;&#1077;&#1097;&#1072; &#1089;&#1072;&#1084;&#1086; &#1090;&#1091;&#1082;.</p>
              <p>&#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103;&#1090; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1077; &#1079;&#1072;&#1097;&#1080;&#1090;&#1077;&#1085; &#1086;&#1090; &#1047;&#1072;&#1082;&#1086;&#1085;&#1072; &#1079;&#1072; &#1047;&#1072;&#1097;&#1080;&#1090;&#1072; &#1085;&#1072; &#1048;&#1079;&#1095;&#1077;&#1079;&#1074;&#1072;&#1097;&#1080;&#1090;&#1077; &#1042;&#1080;&#1076;&#1086;&#1074;&#1077; &#1080; &#1054;&#1088;&#1075;&#1072;&#1085;&#1080;&#1079;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072; &#1079;&#1072; &#1047;&#1072;&#1097;&#1080;&#1090;&#1072; &#1085;&#1072; &#1052;&#1086;&#1088;&#1089;&#1082;&#1080;&#1090;&#1077; &#1041;&#1086;&#1079;&#1072;&#1081;&#1085;&#1080;&#1094;&#1080;. &#1042; &#1084;&#1086;&#1084;&#1077;&#1085;&#1090;&#1072;, &#1087;&#1086;&#1087;&#1091;&#1083;&#1072;&#1094;&#1080;&#1103;&#1090;&#1072; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1089;&#1077; &#1080;&#1079;&#1095;&#1080;&#1089;&#1083;&#1103;&#1074;&#1072; &#1085;&#1072; &#1086;&#1082;&#1086;&#1083;&#1086; 1500. &#1058;&#1098;&#1081; &#1082;&#1072;&#1090;&#1086; &#1077; &#1079;&#1072;&#1089;&#1090;&#1088;&#1072;&#1096;&#1077;&#1085; &#1074;&#1080;&#1076;, &#1077; &#1079;&#1072;&#1073;&#1088;&#1072;&#1085;&#1077;&#1085;&#1086; &#1076;&#1072; &#1085;&#1072;&#1088;&#1072;&#1085;&#1103;&#1074;&#1072;&#1090;&#1077;, &#1087;&#1083;&#1077;&#1085;&#1103;&#1074;&#1072;&#1090;&#1077; &#1080;&#1083;&#1080; &#1091;&#1073;&#1080;&#1074;&#1072;&#1090;&#1077; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;.</p>
              <p>&#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1089;&#1077; &#1088;&#1072;&#1079;&#1084;&#1085;&#1086;&#1078;&#1072;&#1074;&#1072; &#1075;&#1083;&#1072;&#1074;&#1085;&#1086; &#1074; &#1085;&#1077;&#1086;&#1073;&#1080;&#1090;&#1072;&#1077;&#1084;&#1080;&#1090;&#1077; &#1089;&#1077;&#1074;&#1077;&#1088;&#1086;-&#1079;&#1072;&#1087;&#1072;&#1076;&#1085;&#1080; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1089;&#1077; &#1085;&#1072;&#1084;&#1080;&#1088;&#1072;&#1090; &#1089;&#1077;&#1074;&#1077;&#1088;&#1086;- &#1080;&#1079;&#1090;&#1086;&#1095;&#1085;&#1086; &#1086;&#1090; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074; &#1050;&#1072;&#1091;&#1072;&#1081;, &#1085;&#1072;&#1081;- &#1089;&#1077;&#1074;&#1077;&#1088;&#1085;&#1080;&#1103;&#1090; &#1086;&#1090; &#1089;&#1077;&#1076;&#1077;&#1084;&#1090;&#1077; &#1085;&#1072;&#1089;&#1077;&#1083;&#1077;&#1085;&#1080; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1072; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1055;&#1086;&#1085;&#1103;&#1082;&#1086;&#1075;&#1072; &#1090;&#1102;&#1083;&#1077;&#1085;&#1098;&#1090;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1073;&#1098;&#1076;&#1077; &#1079;&#1072;&#1073;&#1077;&#1083;&#1103;&#1079;&#1072;&#1085; &#1076;&#1072; &#1089;&#1080; &#1087;&#1086;&#1095;&#1080;&#1074;&#1072; &#1085;&#1072; &#1073;&#1088;&#1077;&#1075;&#1086;&#1074;&#1077;&#1090;&#1077; &#1085;&#1072; &#1085;&#1103;&#1082;&#1086;&#1081; &#1086;&#1090; &#1085;&#1072;&#1089;&#1077;&#1083;&#1077;&#1085;&#1080;&#1090;&#1077; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1074; &#1095;&#1072;&#1089;&#1090;&#1085;&#1086;&#1089;&#1090; &#1085;&#1072; &#1050;&#1072;&#1091;&#1072;&#1081; &#1080; &#1054;&#1072;&#1093;&#1091;. &#1042;&#1098;&#1087;&#1088;&#1077;&#1082;&#1080; &#1090;&#1086;&#1074;&#1072;, &#1090;&#1102;&#1083;&#1077;&#1085;&#1080;&#1090;&#1077;- &#1084;&#1086;&#1085;&#1072;&#1089;&#1080; &#1085;&#1077; &#1086;&#1089;&#1090;&#1072;&#1074;&#1072;&#1090; &#1090;&#1072;&#1084; &#1079;&#1072; &#1076;&#1098;&#1083;&#1075;&#1086;, &#1090;&#1098;&#1081; &#1082;&#1072;&#1090;&#1086; &#1087;&#1088;&#1077;&#1076;&#1087;&#1086;&#1095;&#1080;&#1090;&#1072;&#1090; 
                &#1076;&#1072; &#1078;&#1080;&#1074;&#1077;&#1103;&#1090; &#1074; &#1091;&#1077;&#1076;&#1080;&#1085;&#1077;&#1085;&#1080;&#1077;.</p>
                
              <p>&#1042;&#1098;&#1079;&#1088;&#1072;&#1089;&#1090;&#1085;&#1080;&#1090;&#1077; &#1090;&#1102;&#1083;&#1077;&#1085;&#1080;- &#1084;&#1086;&#1085;&#1072;&#1089;&#1080; &#1076;&#1086;&#1089;&#1090;&#1080;&#1075;&#1072;&#1090; &#1076;&#1098;&#1083;&#1078;&#1080;&#1085;&#1072; &#1086;&#1082;&#1086;&#1083;&#1086; 7 &#1092;&#1091;&#1090;&#1072; (2.13 &#1084;) &#1080; &#1090;&#1077;&#1075;&#1083;&#1086; &#1084;&#1077;&#1078;&#1076;&#1091; 400 &#1080; 500 &#1087;&#1072;&#1091;&#1085;&#1076;&#1072; (181-226 &#1082;&#1075;). &#1063;&#1077;&#1089;&#1090;&#1086; &#1078;&#1077;&#1085;&#1089;&#1082;&#1080;&#1090;&#1077; &#1089;&#1072; &#1087;&#1086;- &#1075;&#1086;&#1083;&#1077;&#1084;&#1080; &#1086;&#1090; &#1084;&#1098;&#1078;&#1082;&#1080;&#1090;&#1077;. &#1058;&#1077; &#1089;&#1077; &#1093;&#1088;&#1072;&#1085;&#1103;&#1090; &#1089; &#1086;&#1082;&#1090;&#1086;&#1087;&#1086;&#1076;&#1080;, &#1079;&#1084;&#1080;&#1086;&#1088;&#1082;&#1080;, &#1086;&#1084;&#1072;&#1088;&#1080; &#1080; &#1088;&#1080;&#1092;&#1086;&#1074;&#1080; &#1088;&#1080;&#1073;&#1082;&#1080;. &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103; &#1090;&#1102;&#1083;&#1077;&#1085;- &#1084;&#1086;&#1085;&#1072;&#1093; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1089;&#1077; &#1075;&#1084;&#1091;&#1088;&#1082;&#1072; &#1076;&#1086; 600 &#1092;&#1091;&#1090;&#1072; (182 &#1084;) &#1076;&#1098;&#1083;&#1073;&#1086;&#1095;&#1080;&#1085;&#1072; &#1080; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1086;&#1089;&#1090;&#1072;&#1085;&#1077; &#1087;&#1086;&#1076; &#1074;&#1086;&#1076;&#1072;&#1090;&#1072; &#1076;&#1086; 20 &#1084;&#1080;&#1085;&#1091;&#1090;&#1080;.</p>
              <p><strong>&#1061;&#1091;&#1084;&#1091;&#1093;&#1091;&#1084;&#1091;&#1085;&#1091;&#1082;&#1091;&#1085;&#1091;&#1082;&#1091;&#1072;&#1087;&#1091;&#1072;&#1072;</strong></p>
              <p><strong> <img border="0" src="../images/Humuhumunukunukuapuaa.jpg" align="right" alt="Humuhumunukunukuapuaa" width="148" height="125" /></strong>&#1061;&#1091;&#1084;&#1091; 
                - &#1082;&#1072;&#1082;&#1074;&#1086;? &#1047;&#1072; &#1076;&#1072; &#1087;&#1088;&#1086;&#1080;&#1079;&#1085;&#1077;&#1089;&#1077;&#1090;&#1077; &#1080;&#1084;&#1077;&#1090;&#1086; &#1085;&#1072; &#1090;&#1072;&#1079;&#1080; &#1084;&#1077;&#1089;&#1090;&#1085;&#1072; &#1088;&#1080;&#1073;&#1072;, &#1088;&#1072;&#1079;&#1076;&#1077;&#1083;&#1077;&#1090;&#1077; &#1080;&#1084;&#1077;&#1090;&#1086; &#1085;&#1072; 6 &#1095;&#1072;&#1089;&#1090;&#1080;: &#1093;&#1091;&#1084;&#1091; &#1093;&#1091;&#1084;&#1091; &#1085;&#1091;&#1082;&#1091; &#1085;&#1091;&#1082;&#1091; &#1072;&#1087;&#1091;&#1072; &#1072;.&#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1086;&#1090;&#1086; &#1085;&#1072;&#1080;&#1084;&#1077;&#1085;&#1086;&#1074;&#1072;&#1085;&#1080;&#1077; &#1079;&#1072; "&#1073;&#1072;&#1083;&#1080;&#1089;&#1090;&#1086;&#1080;&#1076; &#1089; &#1084;&#1091;&#1094;&#1091;&#1085;&#1072;, &#1085;&#1072;&#1087;&#1086;&#1076;&#1086;&#1073;&#1103;&#1074;&#1072;&#1097;&#1072; &#1090;&#1072;&#1079;&#1080; &#1085;&#1072; &#1087;&#1088;&#1072;&#1089;&#1077;". "&#1061;&#1091;&#1084;&#1091;" &#1077; &#1087;&#1098;&#1089;&#1090;&#1088;&#1086; &#1086;&#1094;&#1074;&#1077;&#1090;&#1077;&#1085;&#1072;, &#1084;&#1077;&#1089;&#1090;&#1085;&#1072; &#1088;&#1080;&#1073;&#1072;, &#1082;&#1086;&#1103;&#1090;&#1086; &#1089;&#1077; &#1088;&#1072;&#1079;&#1084;&#1085;&#1086;&#1078;&#1072;&#1074;&#1072; &#1074;&#1098;&#1074; &#1074;&#1086;&#1076;&#1080;&#1090;&#1077;, 
                &#1086;&#1082;&#1086;&#1083;&#1086; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;.</p>
              <p><strong>&#1052;&#1072;&#1083;&#1072;&#1084;&#1072; &#1080; &#1050;&#1077; &#1082;&#1072;&#1081;</strong></p>
              <p><img border="0" src="../images/underwater-hawaii.jpg" align="left" alt="underwater hawaii" width="148" height="125" />&#1052;&#1072;&#1083;&#1072;&#1084;&#1072; 
                &#1080; &#1050;&#1077; &#1050;&#1072;&#1081;, &#1082;&#1086;&#1077;&#1090;&#1086; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072; "&#1075;&#1088;&#1080;&#1078;&#1077;&#1097; &#1089;&#1077; &#1080;&#1083;&#1080; &#1079;&#1072;&#1097;&#1080;&#1090;&#1072;&#1074;&#1072;&#1097; &#1086;&#1082;&#1077;&#1072;&#1085;&#1072;", &#1077; &#1085;&#1077;&#1088;&#1072;&#1079;&#1076;&#1077;&#1083;&#1085;&#1072; &#1095;&#1072;&#1089;&#1090; &#1086;&#1090; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1076;&#1072;&#1090;&#1080;&#1088;&#1072;&#1097;&#1072; &#1086;&#1097;&#1077; &#1087;&#1088;&#1077;&#1076;&#1080; &#1074;&#1077;&#1082;&#1086;&#1074;&#1077;. &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1072;&#1090;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085; &#1085;&#1072; &#1089;&#1098;&#1090;&#1074;&#1086;&#1088;&#1077;&#1085;&#1080;&#1077;&#1090;&#1086;- &#1082;&#1091;&#1084;&#1091;&#1083;&#1080;&#1087;&#1086;, &#1086;&#1073;&#1103;&#1089;&#1085;&#1103;&#1074;&#1072;, &#1095;&#1077; &#1078;&#1080;&#1074;&#1086;&#1090;&#1098;&#1090; &#1077; &#1079;&#1072;&#1087;&#1086;&#1095;&#1085;&#1072;&#1083; &#1074; &#1084;&#1086;&#1088;&#1077;&#1090;&#1086;, &#1089;&#1098;&#1097;&#1086;, &#1095;&#1077; &#1095;&#1086;&#1074;&#1077;&#1082; &#1077; &#1089;&#1074;&#1098;&#1088;&#1079;&#1072;&#1085; &#1089;&#1098;&#1089; &#1074;&#1089;&#1080;&#1095;&#1082;&#1080; &#1078;&#1080;&#1074;&#1080; &#1089;&#1098;&#1097;&#1077;&#1089;&#1090;&#1074;&#1072;, &#1086;&#1089;&#1086;&#1073;&#1077;&#1085;&#1086; &#1089; &#1084;&#1086;&#1088;&#1089;&#1082;&#1080;&#1103; &#1078;&#1080;&#1074;&#1086;&#1090;. &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1103; &#1072;&#1088;&#1093;&#1080;&#1087;&#1077;&#1083;&#1072;&#1075; &#1085;&#1077; &#1077; &#1086;&#1087;&#1080;&#1089;&#1072;&#1085; &#1082;&#1072;&#1090;&#1086; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080; &#1074; &#1084;&#1086;&#1088;&#1077;