              <? include ("../theme/galleries/this-and-that.html") ?>
              <p>&#1057;&#1083;&#1077;&#1076;&#1074;&#1072;&#1090; &#1085;&#1103;&#1082;&#1086;&#1080; &#1095;&#1077;&#1089;&#1090;&#1086; &#1089;&#1088;&#1077;&#1097;&#1072;&#1085;&#1080; &#1076;&#1091;&#1084;&#1080; &#1080; &#1080;&#1079;&#1088;&#1072;&#1079;&#1080;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1084;&#1086;&#1078;&#1077;&#1090;&#1077; &#1076;&#1072;  &#1095;&#1091;&#1077;&#1090;&#1077;, &#1082;&#1086;&#1075;&#1072;&#1090;&#1086; &#1087;&#1086;&#1089;&#1077;&#1090;&#1080;&#1090;&#1077; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1058;&#1086;&#1074;&#1072; &#1077; &#1088;&#1098;&#1082;&#1086;&#1074;&#1086;&#1076;&#1089;&#1090;&#1074;&#1086; &#1079;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1072;&#1090;&#1072;  &#1082;&#1091;&#1083;&#1090;&#1091;&#1088;&#1072;, &#1090;&#1088;&#1072;&#1076;&#1080;&#1094;&#1080;&#1080; &#1080; &#1086;&#1073;&#1080;&#1095;&#1072;&#1080;, &#1087;&#1086;&#1076;&#1088;&#1077;&#1076;&#1077;&#1085;&#1086; &#1087;&#1086; &#1072;&#1079;&#1073;&#1091;&#1095;&#1077;&#1085; &#1088;&#1077;&#1076;.</p>
              <h2>&#1040;&#1083;&#1086;&#1093;&#1072; '&#1054;&#1077;</h2>
              <p>&#1045;&#1076;&#1085;&#1072; &#1086;&#1090; &#1085;&#1072;&#1081;- &#1080;&#1079;&#1074;&#1077;&#1089;&#1090;&#1085;&#1080;&#1090;&#1077; &#1087;&#1077;&#1089;&#1085;&#1080; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080; &#1077; &#1040;&#1083;&#1086;&#1093;&#1072;  '&#1054;&#1077;, &#1089;&#1098;&#1095;&#1080;&#1085;&#1077;&#1085;&#1072; &#1086;&#1090; &#1082;&#1088;&#1072;&#1083;&#1080;&#1094;&#1072; &#1051;&#1080;&#1083;&#1080;&#1091;&#1086;&#1082;&#1072;&#1083;&#1072;&#1085;&#1080; (1838- 1917). &#1050;&#1088;&#1072;&#1083;&#1080;&#1094;&#1072;&#1090;&#1072; &#1085;&#1072;&#1087;&#1080;&#1089;&#1072;&#1083;&#1072; &#1090;&#1077;&#1082;&#1089;&#1090;&#1072; &#1080;  &#1084;&#1077;&#1083;&#1086;&#1076;&#1080;&#1103;&#1090;&#1072; &#1085;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085;&#1090;&#1072;, &#1082;&#1086;&#1103;&#1090;&#1086; &#1077; &#1073;&#1072;&#1079;&#1080;&#1088;&#1072;&#1085;&#1072; &#1085;&#1072; &#1089;&#1090;&#1072;&#1088;&#1072; &#1092;&#1086;&#1083;&#1082;&#1083;&#1086;&#1088;&#1085;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085; &#1086;&#1090; &#1061;&#1098;&#1088;&#1074;&#1072;&#1090;&#1080;&#1103;  "&#1057;&#1080;&#1076;&#1080; &#1052;&#1072;&#1088;&#1072; &#1085;&#1072; &#1050;&#1072;&#1084;&#1077;&#1085; &#1057;&#1090;&#1091;&#1076;&#1077;&#1085;&#1094;&#1091;". &#1057;&#1093;&#1086;&#1076;&#1089;&#1090;&#1074;&#1086;&#1090;&#1086; &#1084;&#1077;&#1078;&#1076;&#1091; &#1076;&#1074;&#1077;&#1090;&#1077; &#1087;&#1077;&#1089;&#1085;&#1080; &#1077; &#1091;&#1076;&#1080;&#1074;&#1080;&#1090;&#1077;&#1083;&#1085;&#1072;. &#1054;&#1097;&#1077;  &#1087;&#1086;&#1074;&#1077;&#1095;&#1077;: &#1087;&#1088;&#1077;&#1076;&#1087;&#1086;&#1083;&#1072;&#1075;&#1072; &#1089;&#1077;, &#1095;&#1077; &#1095;&#1083;&#1077;&#1085;&#1098;&#1090; &#1085;&#1072; &#1050;&#1088;&#1072;&#1083;&#1089;&#1082;&#1072;&#1090;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1072; &#1075;&#1088;&#1091;&#1087;&#1072;, &#1061;&#1077;&#1085;&#1088;&#1080; &#1041;&#1077;&#1088;&#1075;&#1077;&#1088;,  &#1076;&#1086;&#1085;&#1077;&#1089;&#1098;&#1083; &#1072;&#1074;&#1089;&#1090;&#1088;&#1080;&#1081;&#1089;&#1082;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085;, &#1086;&#1079;&#1072;&#1075;&#1083;&#1072;&#1074;&#1077;&#1085;&#1072; "Die Trane", &#1082;&#1086;&#1103;&#1090;&#1086; &#1080;&#1084;&#1072;&#1083;&#1072; &#1089;&#1098;&#1097;&#1072;&#1090;&#1072; &#1084;&#1077;&#1083;&#1086;&#1076;&#1080;&#1103;.</p>
              <p>&#1050;&#1086;&#1075;&#1072;&#1090;&#1086; &#1082;&#1088;&#1072;&#1083;&#1080;&#1094;&#1072; &#1051;&#1080;&#1083;&#1080;&#1091;&#1086;&#1082;&#1072;&#1083;&#1072;&#1085;&#1080; &#1074;&#1080;&#1076;&#1103;&#1083;&#1072; &#1084;&#1083;&#1072;&#1076;&#1072; &#1076;&#1074;&#1086;&#1081;&#1082;&#1072; &#1076;&#1072; &#1089;&#1077;  &#1087;&#1088;&#1077;&#1075;&#1088;&#1098;&#1097;&#1072;&#1090;, &#1082;&#1072;&#1079;&#1074;&#1072;&#1081;&#1082;&#1080; &#1089;&#1080; &#1076;&#1086;&#1074;&#1080;&#1078;&#1076;&#1072;&#1085;&#1077;, &#1090;&#1103; &#1089;&#1080; &#1087;&#1086;&#1084;&#1080;&#1089;&#1083;&#1080;&#1083;&#1072; &#1079;&#1072; &#1087;&#1088;&#1077;&#1076;&#1089;&#1090;&#1086;&#1103;&#1097;&#1072;&#1090;&#1072; &#1088;&#1072;&#1079;&#1076;&#1103;&#1083;&#1072; &#1085;&#1072;  &#1075;&#1088;&#1091;&#1087;&#1072;&#1090;&#1072; &#1080; &#1089;&#1098;&#1095;&#1080;&#1085;&#1080;&#1083;&#1072; &#1090;&#1072;&#1079;&#1080; &#1087;&#1077;&#1089;&#1077;&#1085;. &#1058;&#1103; &#1073;&#1080;&#1083;&#1072; &#1085;&#1072;&#1087;&#1080;&#1089;&#1072;&#1085;&#1072; &#1089;&#1083;&#1077;&#1076; &#1086;&#1092;&#1080;&#1094;&#1080;&#1072;&#1083;&#1085;&#1086;&#1090;&#1086; &#1087;&#1088;&#1080;&#1089;&#1098;&#1077;&#1076;&#1080;&#1085;&#1103;&#1074;&#1072;&#1085;&#1077;  &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080; &#1082;&#1098;&#1084; &#1057;&#1040;&#1065; &#1087;&#1088;&#1077;&#1079; 1898. &#1044;&#1085;&#1077;&#1089;, &#1040;&#1083;&#1086;&#1093;&#1072; '&#1054;&#1077; &#1095;&#1077;&#1089;&#1090;&#1086; &#1089;&#1077; &#1080;&#1079;&#1087;&#1098;&#1083;&#1085;&#1103;&#1074;&#1072;,  &#1082;&#1086;&#1075;&#1072;&#1090;&#1086; &#1093;&#1086;&#1088;&#1072;&#1090;&#1072; &#1080;&#1089;&#1082;&#1072;&#1090; &#1076;&#1072; &#1089;&#1077; &#1087;&#1086;&#1079;&#1076;&#1088;&#1072;&#1074;&#1103;&#1090; &#1079;&#1072; &#1076;&#1086;&#1073;&#1088;&#1077; &#1076;&#1086;&#1096;&#1083;&#1080;.</p>
              <p><em>Ha'aheo e kua ua i na  pali</em><br />
                (&#1044;&#1098;&#1078;&#1076;&#1098;&#1090; &#1075;&#1086;&#1088;&#1076;&#1086; &#1089;&#1077; &#1080;&#1079;&#1083;&#1080;&#1074;&#1072; &#1085;&#1072;&#1076; &#1089;&#1090;&#1088;&#1098;&#1084;&#1085;&#1080;&#1090;&#1077; &#1089;&#1082;&#1072;&#1083;&#1080;)<br />
                Ke nihi ae la i kanahele<br />
                <em>(&#1048; &#1090;&#1080;&#1093;&#1086; &#1089;&#1077; &#1089;&#1090;&#1080;&#1095;&#1072;  &#1087;&#1086;&#1082;&#1088;&#1072;&#1081; &#1076;&#1098;&#1088;&#1074;&#1077;&#1090;&#1072;&#1090;&#1072;)</em><br />
                E uhai ana paha i ka liko<br />
                (&#1048;&#1079;&#1075;&#1083;&#1077;&#1078;&#1076;&#1072;, &#1095;&#1077; &#1090;&#1098;&#1088;&#1089;&#1080; &#1087;&#1098;&#1087;&#1082;&#1080;&#1090;&#1077;)<br />
                <em>Pua ahihi lehua o uka</em><br />
                (&#1053;&#1072; &#1094;&#1074;&#1077;&#1090;&#1077;&#1090;&#1086; "&#1072;&#1093;&#1080;&#1093;&#1080; &#1083;&#1077;&#1093;&#1091;&#1072;" &#1074; &#1076;&#1086;&#1083;&#1080;&#1085;&#1072;&#1090;&#1072;)<br />
                <em>Aloha 'oe, aloha 'oe</em><br />
                (&#1053;&#1072; &#1076;&#1086;&#1073;&#1098;&#1088; &#1095;&#1072;&#1089;; &#1085;&#1072; &#1076;&#1086;&#1073;&#1098;&#1088; &#1095;&#1072;&#1089;)<br />
                <em>E ke onaona noho i ka  lipo</em><br />
                (&#1058;&#1080;, &#1089;&#1083;&#1072;&#1076;&#1082;&#1086;&#1090;&#1086;, &#1082;&#1086;&#1077;&#1090;&#1086; &#1078;&#1080;&#1074;&#1077;&#1077;&#1096; &#1074; &#1075;&#1086;&#1088;&#1072;&#1090;&#1072;)<br />
                <em>One fond embrace, a  ho'i ae au</em><br />
                (&#1057;&#1077;&#1075;&#1072;, &#1087;&#1088;&#1077;&#1076;&#1080; &#1076;&#1072; &#1090;&#1088;&#1098;&#1075;&#1085;&#1072;)<br />
                &#1044;&#1086;&#1082;&#1072;&#1090;&#1086; &#1089;&#1077; &#1089;&#1088;&#1077;&#1097;&#1085;&#1077;&#1084; &#1086;&#1090;&#1085;&#1086;&#1074;&#1086;</p>
              
              <p><strong>&#1055;&#1077;&#1089;&#1085;&#1080;</strong></p>
              <p>&#1055;&#1077;&#1089;&#1085;&#1080;&#1090;&#1077; &#1089;&#1072; &#1089;&#1098;&#1097;&#1077;&#1089;&#1090;&#1074;&#1077;&#1085;&#1072; &#1095;&#1072;&#1089;&#1090; &#1086;&#1090; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1072;&#1090;&#1072; &#1082;&#1091;&#1083;&#1090;&#1091;&#1088;&#1072;. &#1050;&#1072;&#1090;&#1086; &#1095;&#1072;&#1089;&#1090;  &#1086;&#1090; &#1087;&#1088;&#1072;&#1079;&#1085;&#1077;&#1085;&#1089;&#1090;&#1074;&#1072;, &#1084;&#1086;&#1083;&#1080;&#1090;&#1074;&#1080; &#1080; &#1087;&#1088;&#1080;&#1076;&#1086;&#1073;&#1080;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1076;&#1091;&#1093;&#1086;&#1074;&#1085;&#1072; &#1089;&#1080;&#1083;&#1072;, &#1087;&#1077;&#1089;&#1085;&#1080;&#1090;&#1077; &#1087;&#1088;&#1077;&#1079; &#1075;&#1086;&#1076;&#1080;&#1085;&#1080;&#1090;&#1077; &#1089;&#1072;  &#1073;&#1080;&#1083;&#1080; &#1085;&#1072;&#1095;&#1080;&#1085;, &#1087;&#1086; &#1082;&#1086;&#1081;&#1090;&#1086; &#1093;&#1086;&#1088;&#1072;&#1090;&#1072; &#1089;&#1077; &#1086;&#1073;&#1088;&#1098;&#1097;&#1072;&#1083;&#1080; &#1082;&#1098;&#1084; &#1073;&#1086;&#1075;&#1086;&#1074;&#1077;&#1090;&#1077; &#1089; &#1084;&#1086;&#1083;&#1073;&#1072; &#1076;&#1072; &#1087;&#1088;&#1080;&#1076;&#1086;&#1073;&#1080;&#1103;&#1090; &#1089;&#1080;&#1083;&#1080;  &#1074;&#1098;&#1074; &#1074;&#1089;&#1077;&#1082;&#1080; &#1077;&#1076;&#1080;&#1085; &#1072;&#1089;&#1087;&#1077;&#1082;&#1090; &#1086;&#1090; &#1090;&#1077;&#1093;&#1085;&#1080;&#1103; &#1078;&#1080;&#1074;&#1086;&#1090;. &#1057;&#1084;&#1103;&#1090;&#1072; &#1089;&#1077;, &#1095;&#1077; &#1085;&#1086;&#1089;&#1103;&#1090; &#1076;&#1091;&#1093;&#1086;&#1074;&#1085;&#1072; &#1089;&#1080;&#1083;&#1072;, &#1082;&#1086;&#1103;&#1090;&#1086;  &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1089;&#1083;&#1091;&#1078;&#1080; &#1079;&#1072; &#1074;&#1076;&#1098;&#1093;&#1085;&#1086;&#1074;&#1077;&#1085;&#1080;&#1077; &#1087;&#1086; &#1074;&#1088;&#1077;&#1084;&#1077; &#1085;&#1072; &#1078;&#1080;&#1074;&#1086;&#1090;&#1072; &#1085;&#1072; &#1093;&#1086;&#1088;&#1072;&#1090;&#1072;. &#1052;&#1085;&#1086;&#1075;&#1086; &#1087;&#1077;&#1089;&#1085;&#1080; &#1089;&#1072;  &#1089;&#1095;&#1080;&#1090;&#1072;&#1085;&#1080; &#1079;&#1072; &#1089;&#1074;&#1077;&#1097;&#1077;&#1085;&#1080; &#1080; &#1089;&#1072; &#1087;&#1088;&#1077;&#1076;&#1072;&#1074;&#1072;&#1085;&#1080; &#1086;&#1090; &#1087;&#1086;&#1082;&#1086;&#1083;&#1077;&#1085;&#1080;&#1077; &#1085;&#1072; &#1087;&#1086;&#1082;&#1086;&#1083;&#1077;&#1085;&#1080;&#1077;. &#1053;&#1103;&#1082;&#1086;&#1081; &#1087;&#1077;&#1089;&#1085;&#1080; &#1084;&#1086;&#1078;&#1077;&#1083;&#1080;  &#1076;&#1072; &#1089;&#1077; &#1087;&#1077;&#1103;&#1090; &#1089;&#1072;&#1084;&#1086; &#1086;&#1090; &#1086;&#1087;&#1088;&#1077;&#1076;&#1077;&#1083;&#1077;&#1085;&#1080; &#1089;&#1086;&#1094;&#1080;&#1072;&#1083;&#1085;&#1080; &#1087;&#1088;&#1086;&#1089;&#1083;&#1086;&#1081;&#1082;&#1080;- &#1085;&#1072;&#1081;- &#1095;&#1077;&#1089;&#1090;&#1086; &#1090;&#1086;&#1074;&#1072; &#1073;&#1080;&#1083;&#1080;  &#1089;&#1074;&#1077;&#1097;&#1077;&#1085;&#1080;&#1094;&#1080;&#1090;&#1077;. &#1058;&#1072;&#1082;&#1080;&#1074;&#1072; &#1087;&#1077;&#1089;&#1085;&#1080; &#1073;&#1080;&#1083;&#1086; &#1079;&#1072;&#1073;&#1088;&#1072;&#1085;&#1077;&#1085;&#1086; &#1076;&#1072; &#1089;&#1077; &#1080;&#1079;&#1087;&#1098;&#1083;&#1085;&#1103;&#1074;&#1072;&#1090; &#1086;&#1090; &#1082;&#1086;&#1081;&#1090;&#1086; &#1080; &#1076;&#1072; &#1077; &#1076;&#1088;&#1091;&#1075;.</p>
              <p>&#1055;&#1077;&#1089;&#1085;&#1080;&#1090;&#1077; &#1073;&#1080;&#1083;&#1080; &#1080;&#1079;&#1087;&#1086;&#1083;&#1079;&#1074;&#1072;&#1085;&#1080; &#1079;&#1072; &#1085;&#1103;&#1082;&#1086;&#1083;&#1082;&#1086; &#1094;&#1077;&#1083;&#1080;, &#1085;&#1072;&#1087;&#1088;&#1080;&#1084;&#1077;&#1088; &#1079;&#1072;  &#1074;&#1098;&#1079;&#1093;&#1074;&#1072;&#1083;&#1072;, &#1079;&#1072; &#1087;&#1088;&#1086;&#1084;&#1103;&#1085;&#1072; &#1085;&#1072; &#1086;&#1073;&#1089;&#1090;&#1086;&#1103;&#1090;&#1077;&#1083;&#1089;&#1090;&#1074;&#1072;&#1090;&#1072; &#1074; &#1085;&#1077;&#1095;&#1080;&#1080; &#1078;&#1080;&#1074;&#1086;&#1090;, &#1079;&#1072; &#1087;&#1088;&#1086;&#1082;&#1083;&#1103;&#1090;&#1080;&#1077;, &#1079;&#1072; &#1089;&#1082;&#1088;&#1098;&#1073;,  &#1079;&#1072; &#1079;&#1072;&#1103;&#1074;&#1103;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1083;&#1102;&#1073;&#1086;&#1074; &#1080; &#1084;&#1085;&#1086;&#1075;&#1086; &#1076;&#1088;&#1091;&#1075;&#1080;. &#1057;&#1084;&#1103;&#1090;&#1072;&#1083;&#1086; &#1089;&#1077;, &#1095;&#1077; &#1072;&#1082;&#1086; &#1077;&#1076;&#1085;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085; &#1077; &#1080;&#1079;&#1087;&#1103;&#1090;&#1072;  &#1075;&#1088;&#1077;&#1096;&#1085;&#1086;, &#1090;&#1086;&#1074;&#1072; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1087;&#1088;&#1080;&#1095;&#1080;&#1085;&#1080; &#1091;&#1078;&#1072;&#1089;&#1085;&#1080; &#1087;&#1086;&#1089;&#1083;&#1077;&#1076;&#1080;&#1094;&#1080;. &#1040;&#1082;&#1086; &#1076;&#1086;&#1087;&#1091;&#1089;&#1085;&#1077; &#1075;&#1088;&#1077;&#1096;&#1082;&#1072; &#1074; &#1087;&#1077;&#1077;&#1085;&#1077;&#1090;&#1086;, &#1074;  &#1085;&#1103;&#1082;&#1086;&#1080; &#1089;&#1083;&#1091;&#1095;&#1072;&#1080;, &#1095;&#1086;&#1074;&#1077;&#1082; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1073;&#1098;&#1076;&#1077; &#1085;&#1072;&#1082;&#1072;&#1079;&#1072;&#1085; &#1080; &#1089;&#1098;&#1089; &#1089;&#1084;&#1098;&#1088;&#1090;.</p>
              <p>&#1053;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080; &#1077;&#1079;&#1080;&#1082; &#1076;&#1091;&#1084;&#1072;&#1090;&#1072; &#1079;&#1072; &#1087;&#1077;&#1089;&#1077;&#1085; &#1077; "&#1086;&#1083;&#1080;". &#1054;&#1073;&#1080;&#1082;&#1085;&#1086;&#1074;&#1077;&#1085;&#1086; &#1087;&#1086;&#1076;  &#1092;&#1086;&#1088;&#1084;&#1072;&#1090;&#1072; &#1085;&#1072; &#1089;&#1086;&#1083;&#1086; &#1073;&#1077;&#1079; &#1072;&#1082;&#1086;&#1084;&#1087;&#1072;&#1085;&#1080;&#1084;&#1077;&#1085;&#1090; &#1089;&#1098;&#1089; &#1089;&#1072;&#1084;&#1086; &#1076;&#1074;&#1072; &#1080;&#1083;&#1080; &#1090;&#1088;&#1080; &#1090;&#1086;&#1085;&#1072;. &#1050;&#1086;&#1075;&#1072;&#1090;&#1086; &#1087;&#1077;&#1089;&#1077;&#1085;&#1090;&#1072; &#1089;&#1077;  &#1087;&#1077;&#1077; &#1074;&#1103;&#1088;&#1085;&#1086;, &#1084;&#1072;&#1083;&#1082;&#1086; &#1080;&#1083;&#1080; &#1085;&#1080;&#1082;&#1072;&#1082;&#1098;&#1074; &#1074;&#1098;&#1079;&#1076;&#1091;&#1093; &#1085;&#1077; &#1080;&#1079;&#1083;&#1080;&#1079;&#1072; &#1087;&#1088;&#1077;&#1079; &#1091;&#1089;&#1090;&#1072;&#1090;&#1072;, &#1076;&#1086;&#1082;&#1072;&#1090;&#1086; &#1089;&#1077; &#1087;&#1077;&#1077;. &#1055;&#1086;  &#1090;&#1086;&#1079;&#1080; &#1085;&#1072;&#1095;&#1080;&#1085; &#1087;&#1077;&#1074;&#1077;&#1094;&#1098;&#1090; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1079;&#1072;&#1076;&#1098;&#1088;&#1078;&#1080; &#1076;&#1098;&#1093;&#1072; &#1089;&#1080; &#1087;&#1086; &#1074;&#1088;&#1077;&#1084;&#1077; &#1085;&#1072; &#1076;&#1098;&#1083;&#1075;&#1080;&#1090;&#1077; &#1092;&#1088;&#1072;&#1079;&#1080;. &#1044;&#1085;&#1077;&#1089;,  &#1087;&#1077;&#1089;&#1085;&#1080; &#1089;&#1077; &#1087;&#1077;&#1103;&#1090; &#1087;&#1086; &#1084;&#1085;&#1086;&#1075;&#1086; &#1087;&#1086;&#1074;&#1086;&#1076;&#1080;.&#1053;&#1072;&#1087;&#1088;&#1080;&#1084;&#1077;&#1088; &#1087;&#1086; &#1074;&#1088;&#1077;&#1084;&#1077; &#1085;&#1072; &#1079;&#1072;&#1074;&#1098;&#1088;&#1096;&#1074;&#1072;&#1085;&#1077; &#1085;&#1072; &#1091;&#1095;&#1080;&#1083;&#1080;&#1097;&#1077;, &#1080;&#1083;&#1080;  &#1087;&#1098;&#1082; &#1082;&#1072;&#1090;&#1086; &#1073;&#1083;&#1072;&#1075;&#1086;&#1089;&#1083;&#1086;&#1074;&#1080;&#1103; &#1079;&#1072; &#1085;&#1086;&#1074;&#1072; &#1089;&#1075;&#1088;&#1072;&#1076;&#1072;, &#1080;&#1083;&#1080; &#1087;&#1086; &#1074;&#1088;&#1077;&#1084;&#1077; &#1085;&#1072; &#1087;&#1086;&#1075;&#1088;&#1077;&#1073;&#1077;&#1085;&#1080;&#1077;.</p>
              <h3>&#1044;&#1072; &#1050;&#1072;&#1081;&#1085;</h3>
              <p>&#1044;&#1072; &#1082;&#1080;&#1085;&#1077;, &#1087;&#1088;&#1086;&#1080;&#1079;&#1085;&#1077;&#1089;&#1077;&#1085;&#1086; "&#1076;&#1072;&#1093; &#1082;&#1080;&#1085;&#1077;" &#1077; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1072; &#1088;&#1072;&#1079;&#1075;&#1086;&#1074;&#1086;&#1088;&#1085;&#1072;  &#1092;&#1088;&#1072;&#1079;&#1072;,&#1082;&#1086;&#1103;&#1090;&#1086; &#1097;&#1077; &#1095;&#1091;&#1074;&#1072;&#1090;&#1077; &#1095;&#1077;&#1089;&#1090;&#1086; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1048;&#1089;&#1082;&#1072;&#1090;&#1077; &#1083;&#1080; &#1076;&#1072; &#1079;&#1085;&#1072;&#1077;&#1090;&#1077; &#1082;&#1072;&#1082;&#1074;&#1086;  &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072;? &#1045;, &#1085;&#1072; &#1087;&#1088;&#1072;&#1082;&#1090;&#1080;&#1082;&#1072; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1079;&#1085;&#1072;&#1095;&#1080; &#1074;&#1089;&#1080;&#1095;&#1082;&#1086;. &#1044;&#1072; &#1082;&#1080;&#1085;&#1077; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1089;&#1077; &#1086;&#1090;&#1085;&#1072;&#1089;&#1103; &#1079;&#1072;  &#1095;&#1086;&#1074;&#1077;&#1082;, &#1089;&#1080;&#1090;&#1091;&#1072;&#1094;&#1080;&#1103;, &#1078;&#1080;&#1074;&#1086;&#1090;&#1085;&#1086;, &#1087;&#1083;&#1072;&#1078;, &#1087;&#1077;&#1089;&#1077;&#1085; &#1080;&#1083;&#1080; &#1082;&#1072;&#1082;&#1074;&#1086;&#1090;&#1086; &#1080; &#1076;&#1072; &#1077; &#1076;&#1088;&#1091;&#1075;&#1086;. &#1045;&#1090;&#1086; &#1080; &#1077;&#1076;&#1080;&#1085;  &#1087;&#1088;&#1080;&#1084;&#1077;&#1088;. &#1040;&#1082;&#1086; &#1095;&#1091;&#1077;&#1090;&#1077; &#1085;&#1103;&#1082;&#1086;&#1081; &#1076;&#1072; &#1082;&#1072;&#1079;&#1074;&#1072; "&#1087;&#1086;&#1076;&#1072;&#1081; &#1084;&#1080; &#1076;&#1072; &#1082;&#1080;&#1085;&#1077;" &#1080; &#1087;&#1086;&#1089;&#1086;&#1095;&#1080; &#1082;&#1098;&#1084; &#1085;&#1086;&#1078;&#1080;&#1094;&#1072;, &#1090;&#1086;  &#1090;&#1086;&#1075;&#1072;&#1074;&#1072; &#1090;&#1086;&#1079;&#1080; &#1095;&#1086;&#1074;&#1077;&#1082; &#1087;&#1086;&#1076; "&#1076;&#1072; &#1082;&#1080;&#1085;&#1077;" &#1080;&#1084;&#1072; &#1087;&#1088;&#1077;&#1076;&#1074;&#1080;&#1076; "&#1085;&#1086;&#1078;&#1080;&#1094;&#1072;".</p>
              <p>&#1042; &#1085;&#1103;&#1082;&#1086;&#1080; &#1072;&#1085;&#1075;&#1083;&#1080;&#1081;&#1089;&#1082;&#1080; &#1088;&#1077;&#1095;&#1085;&#1080;&#1094;&#1080; &#1084;&#1086;&#1078;&#1077; &#1076;&#1072; &#1074;&#1080;&#1076;&#1080;&#1090;&#1077; &#1076;&#1072; &#1082;&#1080;&#1085;&#1077;, &#1087;&#1088;&#1077;&#1074;&#1077;&#1076;&#1077;&#1085;&#1086;,  &#1082;&#1072;&#1090;&#1086; "&#1085;&#1077;&#1097;&#1086;&#1090;&#1086;" &#1080;&#1083;&#1080; "&#1090;&#1086;&#1074;&#1072; &#1085;&#1077;&#1097;&#1086;". &#1053;&#1086; &#1080;&#1089;&#1090;&#1080;&#1085;&#1089;&#1082;&#1086;&#1090;&#1086; &#1079;&#1085;&#1072;&#1095;&#1077;&#1085;&#1080;&#1077; &#1077; &#1084;&#1085;&#1086;&#1075;&#1086; &#1087;&#1086;&#1074;&#1077;&#1095;&#1077; &#1086;&#1090; &#1090;&#1086;&#1074;&#1072; &#1080;  &#1082;&#1088;&#1072;&#1089;&#1086;&#1090;&#1072;&#1090;&#1072; &#1085;&#1072; &#1090;&#1086;&#1079;&#1080; &#1080;&#1079;&#1088;&#1072;&#1079; &#1077;, &#1095;&#1077; &#1080;&#1079;&#1075;&#1083;&#1077;&#1078;&#1076;&#1072; &#1074;&#1089;&#1077;&#1082;&#1080; &#1088;&#1072;&#1079;&#1073;&#1080;&#1088;&#1072; &#1079;&#1085;&#1072;&#1095;&#1077;&#1085;&#1080;&#1077;&#1090;&#1086; &#1084;&#1091;. &#1055;&#1088;&#1086;&#1089;&#1090;&#1086;  &#1087;&#1086;&#1089;&#1086;&#1095;&#1077;&#1090;&#1077; &#1085;&#1077;&#1097;&#1086; &#1080; &#1082;&#1072;&#1078;&#1077;&#1090;&#1077; &#1074;&#1098;&#1083;&#1096;&#1077;&#1073;&#1085;&#1080;&#1090;&#1077; &#1076;&#1091;&#1084;&#1080; "&#1076;&#1072; &#1082;&#1080;&#1085;&#1077;" &#1080; &#1074;&#1089;&#1077;&#1082;&#1080; &#1097;&#1077; &#1088;&#1072;&#1079;&#1073;&#1077;&#1088;&#1077; &#1079;&#1072; &#1082;&#1072;&#1082;&#1074;&#1086; &#1084;&#1091;  &#1075;&#1086;&#1074;&#1086;&#1088;&#1080;&#1090;&#1077;.</p>
              <p><strong>&#1055;&#1086;&#1089;&#1086;&#1082;&#1080;</strong></p>
              <p>&#1050;&#1086;&#1075;&#1072;&#1090;&#1086; &#1087;&#1086;&#1087;&#1080;&#1090;&#1072;&#1090;&#1077; &#1079;&#1072; &#1087;&#1086;&#1089;&#1086;&#1082;&#1080; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1084;&#1086;&#1078;&#1077; &#1083;&#1077;&#1089;&#1085;&#1086;  &#1076;&#1072; &#1089;&#1077; &#1089;&#1084;&#1091;&#1090;&#1080;&#1090;&#1077;, &#1082;&#1086;&#1075;&#1072;&#1090;&#1086; &#1084;&#1077;&#1089;&#1090;&#1085;&#1080;&#1090;&#1077; &#1074; &#1086;&#1090;&#1075;&#1086;&#1074;&#1086;&#1088;&#1103;&#1090; "&#1074;&#1098;&#1088;&#1074;&#1080;&#1090;&#1077; &#1082;&#1098;&#1084; &#1044;&#1072;&#1081;&#1084;&#1098;&#1085;&#1076; &#1061;&#1077;&#1076;,  &#1087;&#1088;&#1077;&#1084;&#1080;&#1085;&#1072;&#1074;&#1072;&#1090;&#1077; &#1040;&#1083;&#1072; &#1052;&#1086;&#1072;&#1085;&#1072; &#1080; &#1089;&#1083;&#1077;&#1076; &#1090;&#1086;&#1074;&#1072; &#1089;&#1074;&#1080;&#1074;&#1072;&#1090;&#1077; &#1079;&#1072; &#1084;&#1072;&#1091;&#1082;&#1072;" &#1080;&#1083;&#1080; "&#1082;&#1072;&#1088;&#1072;&#1081;&#1090;&#1077; &#1082;&#1098;&#1084; &#1045;&#1074;&#1072; &#1076;&#1074;&#1077;  &#1084;&#1080;&#1083;&#1080; &#1080; &#1089;&#1083;&#1077;&#1076; &#1090;&#1086;&#1074;&#1072; &#1089;&#1074;&#1080;&#1081;&#1090;&#1077; &#1079;&#1072; &#1084;&#1072;&#1082;&#1072;&#1081;". &#1050;&#1086;&#1075;&#1072;&#1090;&#1086; &#1089;&#1090;&#1077; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;, &#1079;&#1072;&#1073;&#1088;&#1072;&#1074;&#1077;&#1090;&#1077;  &#1079;&#1072; &#1076;&#1091;&#1084;&#1080;&#1090;&#1077; &#1080;&#1079;&#1090;&#1086;&#1082;, &#1079;&#1072;&#1087;&#1072;&#1076;, &#1089;&#1077;&#1074;&#1077;&#1088; &#1080; &#1102;&#1075;. &#1058;&#1086;&#1074;&#1072;, &#1082;&#1086;&#1077;&#1090;&#1086; &#1090;&#1091;&#1082; &#1097;&#1077; &#1095;&#1091;&#1077;&#1090;&#1077;, &#1077; &#1084;&#1072;&#1091;&#1082;&#1072; (&#1082;&#1098;&#1084;  &#1087;&#1083;&#1072;&#1085;&#1080;&#1085;&#1080;&#1090;&#1077;, &#1080;&#1083;&#1080; &#1082;&#1098;&#1084; &#1074;&#1098;&#1090;&#1088;&#1077;&#1096;&#1085;&#1086;&#1089;&#1090;&#1090;&#1072; &#1085;&#1072; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1072;) &#1080; &#1084;&#1072;&#1082;&#1072;&#1081; (&#1082;&#1098;&#1084; &#1086;&#1082;&#1077;&#1072;&#1085;&#1072;). &#1040;&#1082;&#1086; &#1089;&#1090;&#1077; &#1085;&#1072;  &#1086;&#1089;&#1090;&#1088;&#1086;&#1074; &#1054;&#1072;&#1093;&#1091; &#1080; &#1085;&#1103;&#1082;&#1086;&#1081; &#1074;&#1080; &#1082;&#1072;&#1078;&#1077; &#1076;&#1072; &#1086;&#1090;&#1080;&#1076;&#1077;&#1090;&#1077; &#1082;&#1098;&#1084; &#1044;&#1072;&#1081;&#1084;&#1098;&#1085;&#1076; &#1061;&#1077;&#1076;, &#1090;&#1086;&#1074;&#1072; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072; &#1076;&#1072; &#1086;&#1090;&#1080;&#1076;&#1077;&#1090;&#1077;  &#1082;&#1098;&#1084; &#1090;&#1072;&#1079;&#1080; &#1079;&#1072;&#1073;&#1077;&#1083;&#1077;&#1078;&#1080;&#1090;&#1077;&#1083;&#1085;&#1086;&#1089;&#1090; (&#1082;&#1086;&#1103;&#1090;&#1086; &#1089;&#1077; &#1085;&#1072;&#1084;&#1080;&#1088;&#1072; &#1080;&#1079;&#1090;&#1086;&#1095;&#1085;&#1086; &#1086;&#1090; &#1059;&#1072;&#1081;&#1082;&#1080;&#1082;&#1080;). &#1045;&#1074;&#1072; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072; &#1087;&#1086;  &#1087;&#1086;&#1089;&#1086;&#1082;&#1072; &#1085;&#1072; &#1077;&#1076;&#1085;&#1086;&#1080;&#1084;&#1077;&#1085;&#1085;&#1080;&#1103; &#1087;&#1083;&#1072;&#1078;, &#1085;&#1072;&#1084;&#1080;&#1088;&#1072;&#1097; &#1089;&#1077; &#1085;&#1072; &#1102;&#1075;&#1086;&#1079;&#1072;&#1087;&#1072;&#1076;&#1085;&#1080;&#1103; &#1073;&#1088;&#1103;&#1075; &#1085;&#1072; &#1054;&#1072;&#1093;&#1091;.</p>
              
              <p><strong>&#1043;&#1077;&#1082;&#1086;&#1085;</strong></p>
              <p>&#1043;&#1077;&#1082;&#1086;&#1085;&#1098;&#1090; &#1077; &#1095;&#1077;&#1089;&#1090;&#1086; &#1089;&#1088;&#1077;&#1097;&#1072;&#1085;&#1086; &#1078;&#1080;&#1074;&#1086;&#1090;&#1085;&#1086; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1058;&#1086; &#1077;  &#1084;&#1072;&#1083;&#1082;&#1086; &#1074;&#1083;&#1077;&#1095;&#1091;&#1075;&#1086; &#1089; &#1075;&#1086;&#1083;&#1077;&#1084;&#1080; &#1086;&#1095;&#1080; &#1080; &#1083;&#1077;&#1087;&#1082;&#1072;&#1074;&#1080; &#1087;&#1088;&#1098;&#1089;&#1090;&#1080;. &#1044;&#1098;&#1083;&#1075;&#1086; &#1077; &#1086;&#1082;&#1086;&#1083;&#1086; &#1077;&#1076;&#1080;&#1085;- &#1076;&#1074;&#1072; &#1080;&#1085;&#1095;&#1072;. &#1058;&#1086;&#1074;&#1072;  &#1077; &#1076;&#1088;&#1091;&#1078;&#1077;&#1083;&#1102;&#1073;&#1085;&#1086; &#1089;&#1098;&#1079;&#1076;&#1072;&#1085;&#1080;&#1077;, &#1082;&#1086;&#1077;&#1090;&#1086; &#1086;&#1073;&#1080;&#1095;&#1072; &#1076;&#1072; &#1078;&#1080;&#1074;&#1077;&#1077; &#1074; &#1082;&#1098;&#1097;&#1080;&#1090;&#1077; &#1085;&#1072; &#1093;&#1086;&#1088;&#1072;&#1090;&#1072;. &#1044;&#1086;&#1088;&#1080; &#1080; &#1076;&#1072; &#1085;&#1077;  &#1086;&#1073;&#1080;&#1095;&#1072;&#1090;&#1077; &#1084;&#1072;&#1083;&#1082;&#1080; &#1078;&#1080;&#1074;&#1086;&#1090;&#1080;&#1085;&#1095;&#1077;&#1090;&#1072; &#1082;&#1072;&#1090;&#1086; &#1090;&#1103;&#1093;, &#1086;&#1087;&#1080;&#1090;&#1072;&#1081;&#1090;&#1077; &#1089;&#1077; &#1076;&#1072; &#1085;&#1077; &#1089;&#1077; &#1087;&#1083;&#1072;&#1096;&#1080;&#1090;&#1077; &#1086;&#1090; &#1075;&#1077;&#1082;&#1086;&#1085;&#1072;.</p>
              <p>&#1044;&#1086;&#1073;&#1088;&#1072;&#1090;&#1072; &#1080;&#1084; &#1089;&#1090;&#1088;&#1072;&#1085;&#1072; &#1077;, &#1095;&#1077; &#1080;&#1079;&#1103;&#1078;&#1076;&#1072;&#1090; &#1084;&#1085;&#1086;&#1075;&#1086;&#1082;&#1088;&#1072;&#1090;&#1085;&#1086; &#1087;&#1086;&#1074;&#1077;&#1095;&#1077; &#1073;&#1091;&#1073;&#1086;&#1083;&#1077;&#1095;&#1082;&#1080;  &#1086;&#1090; &#1089;&#1086;&#1073;&#1089;&#1090;&#1074;&#1077;&#1085;&#1086;&#1090;&#1086; &#1089;&#1080; &#1090;&#1077;&#1075;&#1083;&#1086;. &#1047;&#1072;&#1090;&#1086;&#1074;&#1072; &#1086;&#1089;&#1090;&#1072;&#1074;&#1077;&#1090;&#1077; &#1075;&#1080; &#1085;&#1072;&#1084;&#1080;&#1088;&#1072; &#1080; &#1075;&#1083;&#1077;&#1076;&#1072;&#1081;&#1090;&#1077; &#1082;&#1072;&#1082; &#1097;&#1077; &#1080;&#1079;&#1103;&#1076;&#1072;&#1090;  &#1085;&#1072;&#1089;&#1077;&#1082;&#1086;&#1084;&#1080;&#1090;&#1077; &#1080; &#1093;&#1083;&#1077;&#1073;&#1072;&#1088;&#1082;&#1080;&#1090;&#1077;, &#1082;&#1086;&#1080;&#1090;&#1086; &#1078;&#1080;&#1074;&#1077;&#1103;&#1090; &#1074; &#1082;&#1098;&#1097;&#1072;&#1090;&#1072; &#1074;&#1080; &#1080; &#1086;&#1082;&#1086;&#1083;&#1086; &#1085;&#1077;&#1103;. &#1054;&#1089;&#1074;&#1077;&#1085; &#1090;&#1086;&#1074;&#1072;, &#1076;&#1072;  &#1091;&#1073;&#1080;&#1077;&#1096; &#1075;&#1077;&#1082;&#1086;&#1085; &#1077; &#1089;&#1095;&#1080;&#1090;&#1072;&#1085;&#1086; &#1079;&#1072; &#1083;&#1086;&#1096; &#1082;&#1098;&#1089;&#1084;&#1077;&#1090; &#1074; &#1061;&#1072;&#1074;&#1072;&#1081;. &#1047;&#1072; &#1084;&#1085;&#1086;&#1075;&#1086; &#1093;&#1072;&#1074;&#1072;&#1081;&#1094;&#1080;, &#1075;&#1077;&#1082;&#1086;&#1085;&#1098;&#1090; &#1077;  &#1072;&#1091;&#1084;&#1072;&#1082;&#1091;&#1072; &#1080;&#1083;&#1080; &#1089;&#1077;&#1084;&#1077;&#1077;&#1085; &#1073;&#1086;&#1075;.</p>
              <p><strong>&#1061;&#1077;&#1080;&#1072;&#1091;</strong></p>
              <p>&#1061;&#1077;&#1080;&#1072;&#1091; &#1089;&#1072; &#1076;&#1088;&#1077;&#1074;&#1085;&#1080; &#1093;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080; &#1093;&#1088;&#1072;&#1084;&#1086;&#1074;&#1077;. &#1047;&#1072; &#1084;&#1085;&#1086;&#1075;&#1086; &#1093;&#1072;&#1074;&#1072;&#1081;&#1094;&#1080;, &#1090;&#1086;&#1074;&#1072; &#1089;&#1072;  &#1084;&#1077;&#1089;&#1090;&#1072;, &#1087;&#1088;&#1080;&#1090;&#1077;&#1078;&#1072;&#1074;&#1072;&#1097;&#1080; &#1086;&#1075;&#1088;&#1086;&#1084;&#1085;&#1072; &#1089;&#1080;&#1083;&#1072;. &#1058;&#1077; &#1073;&#1080;&#1083;&#1080; &#1087;&#1086;&#1089;&#1090;&#1088;&#1086;&#1077;&#1085;&#1080; &#1089; &#1084;&#1085;&#1086;&#1075;&#1086; &#1079;&#1076;&#1088;&#1072;&#1074;&#1080; &#1089;&#1090;&#1077;&#1085;&#1080;,  &#1086;&#1073;&#1082;&#1088;&#1098;&#1078;&#1072;&#1074;&#1072;&#1097;&#1080; &#1088;&#1072;&#1074;&#1085;&#1080; &#1079;&#1077;&#1084;&#1080;, &#1087;&#1086;&#1082;&#1088;&#1080;&#1090;&#1080; &#1089; &#1087;&#1083;&#1086;&#1089;&#1082;&#1080; &#1082;&#1072;&#1084;&#1098;&#1095;&#1077;&#1090;&#1072;. &#1047;&#1072;&#1076; &#1089;&#1090;&#1077;&#1085;&#1080;&#1090;&#1077;, &#1074; &#1094;&#1077;&#1085;&#1090;&#1098;&#1088;&#1072; &#1085;&#1072;  &#1093;&#1077;&#1080;&#1072;&#1091;, &#1084;&#1085;&#1086;&#1075;&#1086; &#1095;&#1077;&#1089;&#1090;&#1086; &#1073;&#1080;&#1083;&#1080; &#1087;&#1086;&#1089;&#1090;&#1088;&#1086;&#1103;&#1074;&#1072;&#1085;&#1080; &#1078;&#1077;&#1088;&#1090;&#1074;&#1077;&#1085;&#1080;&#1094;&#1080; &#1080; &#1084;&#1072;&#1083;&#1082;&#1080; &#1089;&#1075;&#1088;&#1072;&#1076;&#1080;, &#1082;&#1088;&#1080;&#1077;&#1097;&#1080;  &#1072;&#1088;&#1090;&#1077;&#1092;&#1072;&#1082;&#1090;&#1080;.</p>
              <p>&#1061;&#1077;&#1080;&#1072;&#1091; &#1089;&#1072; &#1073;&#1080;&#1083;&#1080; &#1089;&#1090;&#1088;&#1086;&#1077;&#1085;&#1080; &#1079;&#1072; &#1088;&#1072;&#1079;&#1083;&#1080;&#1095;&#1085;&#1080; &#1094;&#1077;&#1083;&#1080;. &#1061;&#1077;&#1080;&#1072;&#1091; &#1048;&#1091;&#1072;&#1082;&#1080;&#1085;&#1080; &#1073;&#1080;&#1083;&#1080;  &#1087;&#1086;&#1089;&#1090;&#1088;&#1086;&#1077;&#1085;&#1080; &#1086;&#1090; &#1074;&#1098;&#1088;&#1093;&#1086;&#1074;&#1085;&#1080; &#1075;&#1083;&#1072;&#1074;&#1085;&#1086;&#1082;&#1086;&#1084;&#1072;&#1085;&#1076;&#1074;&#1072;&#1097;&#1080; &#1080; &#1080;&#1079;&#1087;&#1086;&#1083;&#1079;&#1074;&#1072;&#1085;&#1080; &#1079;&#1072; &#1095;&#1086;&#1074;&#1077;&#1096;&#1082;&#1080;  &#1078;&#1077;&#1088;&#1090;&#1074;&#1086;&#1087;&#1088;&#1080;&#1085;&#1086;&#1096;&#1077;&#1085;&#1080;&#1103;. &#1061;&#1077;&#1080;&#1072;&#1091; &#1093;&#1086;&#1086;&#1083;&#1072; &#1073;&#1080;&#1083;&#1080; &#1083;&#1077;&#1095;&#1077;&#1073;&#1085;&#1080; &#1093;&#1088;&#1072;&#1084;&#1086;&#1074;&#1077;, &#1072; &#1087;&#1091;&#1091;&#1093;&#1086;&#1085;&#1091;&#1072; &#1073;&#1080;&#1083;&#1080; &#1084;&#1077;&#1089;&#1090;&#1072; &#1079;&#1072;  &#1087;&#1086;&#1076;&#1089;&#1083;&#1086;&#1085;, &#1082;&#1098;&#1076;&#1077;&#1090;&#1086; &#1087;&#1088;&#1077;&#1089;&#1090;&#1098;&#1087;&#1085;&#1080;&#1094;&#1080; &#1084;&#1086;&#1078;&#1077;&#1083;&#1080; &#1076;&#1072; &#1085;&#1072;&#1084;&#1077;&#1088;&#1103;&#1090; &#1091;&#1073;&#1077;&#1078;&#1080;&#1097;&#1077;.</p>
              <p>&#1044;&#1085;&#1077;&#1089;, &#1088;&#1091;&#1080;&#1085;&#1080; &#1093;&#1077;&#1080;&#1072;&#1091; &#1084;&#1086;&#1075;&#1072;&#1090; &#1076;&#1072; &#1073;&#1098;&#1076;&#1072;&#1090; &#1086;&#1090;&#1082;&#1088;&#1080;&#1090;&#1080; &#1085;&#1072;&#1074;&#1089;&#1103;&#1082;&#1098;&#1076;&#1077; &#1087;&#1086;  &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1040;&#1082;&#1086; &#1080;&#1089;&#1082;&#1072;&#1090;&#1077; &#1076;&#1072; &#1087;&#1086;&#1082;&#1072;&#1078;&#1077;&#1090;&#1077; &#1091;&#1074;&#1072;&#1078;&#1077;&#1085;&#1080;&#1077;&#1090;&#1086; &#1089;&#1080;, &#1085;&#1072;&#1087;&#1088;&#1072;&#1074;&#1077;&#1090;&#1077; &#1090;&#1086;&#1074;&#1072;, &#1082;&#1086;&#1077;&#1090;&#1086;  &#1087;&#1088;&#1072;&#1074;&#1103;&#1090; &#1080; &#1084;&#1077;&#1089;&#1090;&#1085;&#1080;&#1090;&#1077; &#1080; &#1086;&#1089;&#1090;&#1072;&#1074;&#1077;&#1090;&#1077; &#1074; &#1093;&#1088;&#1072;&#1084;&#1072; &#1084;&#1072;&#1083;&#1098;&#1082; &#1076;&#1072;&#1088;, &#1082;&#1072;&#1090;&#1086; &#1093;&#1088;&#1072;&#1085;&#1072; &#1080;&#1083;&#1080; &#1084;&#1072;&#1083;&#1098;&#1082; &#1082;&#1072;&#1084;&#1098;&#1082;,  &#1091;&#1074;&#1080;&#1090; &#1074; &#1083;&#1080;&#1089;&#1090;&#1086; "&#1090;&#1080;".</p>
              <p><strong>&#1050;&#1072;&#1087;&#1091;</strong></p>
              <p>&#1050;&#1072;&#1087;&#1091; &#1086;&#1079;&#1085;&#1072;&#1095;&#1072;&#1074;&#1072; &#1085;&#1077;&#1097;&#1086; &#1079;&#1072;&#1073;&#1088;&#1072;&#1085;&#1077;&#1085;&#1086;, &#1090;&#1072;&#1073;&#1091; &#1085;&#1072; &#1061;&#1072;&#1074;&#1072;&#1081;&#1089;&#1082;&#1080;&#1090;&#1077; &#1086;&#1089;&#1090;&#1088;&#1086;&#1074;&#1080;. &#1042;  &#1044;&#1088;&#1077;&#1074;&#1085;&#1086;&#1089;&#1090;&#1090;&#1072; &#1089;&#1098;&#1097;&#1077;&#1089;&#1090;&#1074;&#1091;&#1074;&#1072;&#1083;&#1072; &#1089;&#1080;&#1089;&#1090;&#1077;&#1084;&#1072; &#1082;&#1072;&#1087;&#1091;, &#1088;&#1077;&#1075;&#1091;&#1083;&#1080;&#1088;&#1072;&#1097;&#1072; &#1077;&#1078;&#1077;&#1076;&#1085;&#1077;&#1074;&#1085;&#1086; &#1086;&#1090;&#1085;&#1086;&#1096;&#1077;&#1085;&#1080;&#1103;&#1090;&#1072; &#1076;&#1072; &#1073;&#1098;&#1076;&#1072;&#1090;  &#1074; &#1076;&#1072;&#1076;&#1077;&#1085;&#1080; &#1085;&#1086;&#1088;&#1084;&#1080; &#1079;&#1072; &#1074;&#1089;&#1077;&#1082;&#1080; &#1087;&#1086;&#1083; &#1080; &#1089;&#1086;&#1094;&#1080;&#1072;&#1083;&#1077;&#1085; &#1082;&#1083;&#1072;&#1089;. &#1058;&#1103; &#1086;&#1089;&#1080;&#1075;&#1091;&#1088;&#1080;&#1083;&#1072; &#1087;&#1088;&#1072;&#1074;&#1080;&#1083;&#1072; &#1079;&#1072; &#1078;&#1080;&#1074;&#1086;&#1090; &#1074;  &#1086;&#1073;&#1097;&#1077;&#1089;&#1090;&#1074;&#1077;&#1085;&#1072;&#1090;&#1072; &#1089;&#1090;&#1088;&#1091;&#1082;&#1090;&#1091;&#1088;&#1072;. &#1053;&#1072;&#1087;&#1088;&#1080;&#1084;&#1077;&#1088; &#1080;&#1084;&#1072;&#1083;&#1086; &#1082;&#1072;&#1087;&#1091; &#1079;&#1072; &#1086;&#1073;&#1080;&#1082;&#1085;&#1086;&#1074;&#1077;&#1085;&#1080;&#1090;&#1077; &#1075;&#1088;&#1072;&#1078;&#1076;&#1072;&#1085;&#1080; &#1076;&#1072; &#1089;&#1090;&#1086;&#1103;&#1090;  &#1087;&#1088;&#1072;&#1074;&#1080; &#1074; &#1087;&#1088;&#1080;&#1089;&#1098;&#1089;&#1090;&#1074;&#1080;&#1077;&#1090;&#1086; &#1085;&#1072; &#1088;&#1098;&#1082;&#1086;&#1074;&#1086;&#1076;&#1080;&#