              <? include ("../../theme/galleries/bigislandhome.html") ?>
              <p>&#24744;&#22909;&#65292;&#27426;&#36814;&#26469;&#21040;&#22799;&#23041;&#22839;&#65292;&#20197;&#21450;&#21517;&#21103;&#20854;&#23454;&#30340;&#22799;&#23041;&#20202;"&#22823;&#23707;"&#12290;&#22823;&#23707;&#26159;&#25152;&#26377;&#25152;&#26377;&#22799;&#23041;&#22839;&#23707;&#23679;&#20013;&#26368;&#22823;&#30340;&#19968;&#20010;&#65292;&#30456;&#24403;&#20110;&#20854;&#23427;&#20027;&#35201;&#23707;&#23679;&#38754;&#31215;&#24635;&#21512;&#30340;&#20108;&#20493;&#65292;&#23427;&#20957;&#32858;&#20102;&#22799;&#23041;&#22839;&#30340;&#20840;&#37096;&#31934;&#21326;&#12290;&#22799;&#23041;&#20202;&#25345;&#32493;&#25193;&#24352;&#30340;&#33021;&#21147;&#29978;&#33267;&#36229;&#36807;&#20102;&#23427;&#30340;&#24778;&#20154;&#20043;&#32654;&#65292;&#36825;&#20027;&#35201;&#26159;&#30001;&#20110;&#19990;&#30028;&#19978;&#26368;&#27963;&#36291;&#30340;&#28779;&#23665;――&#22522;&#25289;&#38886;&#21380;&#23665;&#19981;&#26029;&#21943;&#21457;&#65292;&#21046;&#36896;&#20986;&#30340;&#29076;&#27974;&#25345;&#32493;&#27969;&#21160;&#36896;&#25104;&#30340;&#12290;</p>
              <p>&#22823;&#23707;&#34987;&#35748;&#20026;&#26159;&#22799;&#23041;&#22839;&#24030;&#39640;&#23572;&#22827;&#36816;&#21160;&#30340;&#22825;&#22530;&#65292;&#20849;&#26377;&#65298;&#65296;&#20010;&#39118;&#26684;&#21508;&#24322;&#65292;&#26497;&#36171;&#25361;&#25112;&#24615;&#30340;&#29699;&#22330;&#12290;&#23427;&#21516;&#26102;&#21448;&#20197;&#22799;&#23041;&#22839;&#30340;&#22402;&#38035;&#39318;&#24220;&#32780;&#33879;&#31216;&#12290;&#22312;&#36825;&#37324;&#24744;&#21487;&#20197;&#21442;&#35266;&#26449;&#33853;&#20013;&#26368;&#22823;&#30340;&#31169;&#20154;&#20892;&#22330;&#65288;Parker  Ranch&#65292;&#24085;&#20811;&#20892;&#22330;&#65289;&#65292;&#24744;&#21487;&#20197;&#22312;&#39640;&#26723;&#30340;&#28023;&#28392;&#28193;&#20551;&#26449;&#20241;&#24687;&#65292;&#24744;&#36824;&#21487;&#20197;&#21442;&#35266;&#22799;&#23041;&#22839;&#26368;&#21518;&#19968;&#20010;&#26412;&#22303;&#28180;&#26449;&#65288;Milolii&#65292;&#23494;&#32599;&#21033;&#20234;&#65289;&#65292;&#24182;&#22312;&#28779;&#23665;&#36816;&#21160;&#32487;&#21457;&#30340;&#26102;&#20505;&#65292;&#20110;&#28145;&#22812;&#25506;&#23519;&#31070;&#31192;&#32780;&#28861;&#28909;&#30340;&#23721;&#27974;&#12290;&#22823;&#23707;&#19978;&#20016;&#23500;&#22810;&#26679;&#30340;&#27963;&#21160;&#21644;&#33258;&#28982;&#26223;&#35266;&#30340;&#30830;&#26159;&#26080;&#19982;&#20262;&#27604;&#12290;</p>
              
              <p><strong>&#22320;&#29702;&#26465;&#20214;&#21450;&#27668;&#20505;</strong></p>
              <p>&#22823;&#23707;&#23707;&#22522;&#20026;4,038&#24179;&#26041;&#31859;&#65292;&#21344;&#22799;&#23041;&#22839;&#24030;&#24635;&#38754;&#31215;&#30340;&#65302;&#65298;&#65285;&#65292;&#20854;&#25193;&#23637;&#36895;&#24230;&#25490;&#21517;&#31532;&#20108;&#12290;&#30001;&#20110;&#33258;&#28982;&#29615;&#22659;&#22810;&#21464;&#65292;&#22799;&#23041;&#22839;&#34987;&#35748;&#20026;&#26159;&#25152;&#26377;&#23707;&#23679;&#20013;&#29983;&#29289;&#22810;&#26679;&#24615;&#26368;&#20026;&#22797;&#26434;&#30340;&#22320;&#24102;&#12290;&#21916;&#27426;&#36827;&#34892;&#22810;&#31181;&#25143;&#22806;&#27963;&#21160;&#30340;&#28216;&#23458;&#22312;&#22823;&#23707;&#19968;&#23450;&#19981;&#20250;&#24863;&#21040;&#20047;&#21619;&#12290;</p>
              <p>&#36825;&#37324;&#30340;&#27668;&#28201;&#24179;&#22343;&#22312;&#22312;&#21326;&#27663;71&#21040;87&#24230;&#20043;&#38388;&#65288;&#25668;&#27663;&#30340;21.6&#21040;25&#24230;&#65289;&#65292;&#38543;&#30528;&#24744;&#19978;&#34892;&#27493;&#20837;&#23665;&#22320;&#65292;&#27668;&#28201;&#30456;&#24212;&#19979;&#38477;&#12290;&#22312;&#39030;&#37096;&#30001;&#30333;&#38634;&#35206;&#30422;&#30340;&#20882;&#32435;&#20975;&#20122;&#28779;&#23665;&#65292;&#27668;&#28201;&#26102;&#24120;&#39588;&#38477;&#21040;&#38646;&#24230;&#20197;&#19979;&#12290;&#22823;&#23707;&#21253;&#21547;&#20102;&#19990;&#30028;13&#31181;&#27668;&#20505;&#24102;&#20013;&#30340;11&#20010;&#65292;&#22240;&#27492;&#22914;&#26524;&#24744;&#25171;&#31639;&#22312;&#28779;&#23665;&#19978;&#36828;&#36275;&#65292;&#35831;&#25552;&#21069;&#20934;&#22791;&#22909;&#21508;&#31181;&#34915;&#29289;&#12290;&#22914;&#26524;&#21508;&#31181;&#27668;&#20505;&#37117;&#33021;&#27491;&#24120;&#26174;&#29616;&#30340;&#35805;&#65292;&#26377;&#22235;&#36718;&#39537;&#36710;&#30340;&#28369;&#38634;&#19987;&#23478;&#23601;&#21487;&#20197;&#36866;&#26102;&#23545;&#33258;&#24049;&#25552;&#20986;&#25361;&#25112;&#20102;&#65281;</p>
              <p><strong>&#65301;&#24231;&#28779;&#23665;</strong></p>
              <p>&#22799;&#23041;&#22839;&#23707;&#30001;5&#24231;&#26368;&#21021;&#30028;&#38480;&#20998;&#26126;&#30340;&#28779;&#23665;&#26500;&#25104;&#65292;&#32463;&#36807;&#25509;&#36830;&#19981;&#26029;&#30340;&#21943;&#21457;&#65292;&#23548;&#33268;&#21508;&#20010;&#28779;&#23665;&#24444;&#27492;&#20132;&#21472;&#65292;&#26368;&#32456;&#24418;&#25104;&#21516;&#19968;&#22359;&#22303;&#22320;&#12290;&#24744;&#21487;&#20197;&#36890;&#36807;&#28216;&#35272;&#26607;&#21704;&#25289;&#65288;Kohala&#65289;&#65292;&#20882;&#32435;&#20975;&#20122;&#65288;Mauna Kea&#65289;&#65292;&#38669;&#38463;&#25289;&#25289;&#65288;Hualalai&#65289;&#65292;&#20882;&#32435;&#32599;&#20122;&#65288;Mauna Loa&#65289;&#65292;&#21644;&#22522;&#36797;&#20122;&#65288;Kilauea&#65289;&#28779;&#23665;&#26356;&#20026;&#30452;&#35266;&#22320;&#20102;&#35299;&#22799;&#23041;&#22839;&#20016;&#23500;&#30340;&#22320;&#29702;&#21382;&#21490;&#12290;&#20882;&#32435;&#32599;&#20122;&#65288;Mauna  Loa&#65289;&#65292;&#19990;&#30028;&#19978;&#26368;&#22823;&#30340;&#28779;&#23665;&#65292;&#21644;&#22522;&#36797;&#20122;&#65288;Kilauea&#65289;&#65292;&#19990;&#30028;&#19978;&#26368;&#27963;&#36291;&#30340;&#28779;&#23665;&#65292;&#23427;&#20204;&#37117;&#26159;&#27963;&#28779;&#23665;&#65292;&#23613;&#31649;&#20882;&#32435;&#32599;&#20122;&#20174;&#65297;&#65305;&#65304;&#65300;&#24180;&#20197;&#26469;&#20877;&#27809;&#26377;&#21943;&#21457;&#36807;&#12290;&#20882;&#32435;&#20975;&#20122;&#65288;Mauna  Kea&#65289;&#28779;&#23665;&#34987;&#35748;&#20026;&#26159;&#19990;&#30028;&#19978;&#26368;&#39640;&#30340;&#23665;&#65292;&#20174;&#24213;&#37096;&#21040;&#39030;&#37096;&#20849;&#65299;&#65298;&#65292;&#65296;&#65296;&#65296;&#33521;&#23610;&#65288;&#65305;&#65292;&#65303;&#65301;&#65299;&#31859;&#65289;&#12290;&#20882;&#32435;&#20975;&#20122;&#27668;&#35937;&#22825;&#25991;&#21488;&#26159;&#22825;&#25991;&#23398;&#30740;&#31350;&#30340;&#37325;&#35201;&#22522;&#22320;――&#31449;&#22312;&#20882;&#32435;&#