            <p><strong>&#1050;&#1086;&#1083;&#1083;&#1077;&#1076;&#1078; &#1061;&#1101;&#1083;&#1076; &#1074;  &#1043;&#1086;&#1085;&#1086;&#1083;&#1091;&#1083;&#1091; (Heald College)</strong> - &#1085;&#1077;&#1075;&#1086;&#1089;&#1091;&#1076;&#1072;&#1088;&#1089;&#1090;&#1074;&#1077;&#1085;&#1085;&#1086;&#1077;, &#1085;&#1077;&#1082;&#1086;&#1084;&#1084;&#1077;&#1088;&#1095;&#1077;&#1089;&#1082;&#1086;&#1077; &#1091;&#1095;&#1077;&#1073;&#1085;&#1086;&#1077;  &#1079;&#1072;&#1074;&#1077;&#1076;&#1077;&#1085;&#1080;&#1077;. &#1041;&#1099;&#1083; &#1086;&#1089;&#1085;&#1086;&#1074;&#1072;&#1085; &#1074; 1993 &#1075;&#1086;&#1076;&#1091; &#1080; &#1088;&#1072;&#1089;&#1087;&#1086;&#1083;&#1086;&#1078;&#1077;&#1085; &#1074; &#1088;&#1072;&#1081;&#1086;&#1085;&#1077; &#1040;&#1083;&#1072;-&#1052;&#1086;&#1072;&#1085;&#1072;. &#1050;&#1086;&#1083;&#1083;&#1077;&#1076;&#1078;  &#1087;&#1088;&#1077;&#1076;&#1083;&#1072;&#1075;&#1072;&#1077;&#1090; &#1086;&#1073;&#1091;&#1095;&#1077;&#1085;&#1080;&#1077; &#1074; &#1089;&#1092;&#1077;&#1088;&#1077; &#1073;&#1080;&#1079;&#1085;&#1077;&#1089;&#1072;, &#1090;&#1077;&#1093;&#1085;&#1086;&#1083;&#1086;&#1075;&#1080;&#1080; &#1080; &#1079;&#1076;&#1088;&#1072;&#1074;&#1086;&#1086;&#1093;&#1088;&#1072;&#1085;&#1077;&#1085;&#1080;&#1103;. &#1057;&#1088;&#1077;&#1076;&#1085;&#1077;&#1077;  &#1082;&#1086;&#1083;&#1080;&#1095;&#1077;&#1089;&#1090;&#1074;&#1086; &#1089;&#1090;&#1091;&#1076;&#1077;&#1085;&#1090;&#1086;&#1074; &#1079;&#1072; &#1089;&#1077;&#1084;&#1077;&#1089;&#1090;&#1088; - 900 &#1095;&#1077;&#1083;&#1086;&#1074;&#1077;&#1082;. &#1053;&#1072; &#1082;&#1086;&#1085;&#1090;&#1080;&#1085;&#1077;&#1085;&#1090;&#1072;&#1083;&#1100;&#1085;&#1086;&#1081; &#1095;&#1072;&#1089;&#1090;&#1080; &#1057;&#1064;&#1040;  &#1090;&#1072;&#1082;&#1078;&#1077; &#1084;&#1086;&#1078;&#1085;&#1086; &#1085;&#1072;&#1081;&#1090;&#1080; &#1077;&#1097;&#1077; 10 &#1082;&#1086;&#1083;&#1083;&#1077;&#1076;&#1078;&#1077;&#1081; &#1101;&#1090;&#1086;